Expression<Func<OfferMailingSubscription, bool>> filter = s => EF.Functions.ILike(s.Email, subs.Email);

if (!string.IsNullOrWhiteSpace(subs.Location))
    filter = HLPredicateBuilder.And(filter, s => EF.Functions.ILike(s.Location, subs.Location));

if (!string.IsNullOrWhiteSpace(subs.Position))
    filter = HLPredicateBuilder.And(filter, s => EF.Functions.ILike(s.Position, subs.Position));

